gc.fillInfo = function (data) {
    var t = "";
    t += '<li class="notice">';
    if (data.f) {
        if (location.hash.indexOf("game/") > -1) {
            data.gameid = location.hash.split("/")[1]
        }
        t += '<a class="flex" onclick="gc.getInfoContent(' + data.id + ",'" + data.title + "'," + data.inputtime * 1e3 + ",'" + location.hash + "'," + data.gameid + ')" href="javascript:;">'
    } else {
        t += '<a class="flex" href="' + data.link_url + '">'
    } if (data.labels.indexOf(1) >= 0) {
        t += '<span><i class="tags-new">新游</i></span>'
    }
    if (data.labels.indexOf(2) >= 0) {
        t += '<span><i class="tags-recommend">活动</i></span>'
    }
    if (data.labels.indexOf(3) >= 0) {
        t += '<span><i class="tags-hot">新服</i></span>'
    }
    if (data.labels.indexOf(4) >= 0) {
        t += '<span><i class="tags-elite">攻略</i></span>'
    }
    if (data.labels.indexOf(5) >= 0) {
        t += '<span><i class="tags-coupon">公告</i></span>'
    }
    t += '<span class="flex-list">' + data.title + "</span>";
    t += "<span>" + new Date(data.inputtime * 1e3).format("yyyy-MM-dd") + "</span>";
    t += "</a>";
    t += "</li>";
    return t
};
gc.fillGift = function (data, id, url, callback) {
    var t = "";
    if (callback) {
        t += '<li class="gift">';
        t += '<div class="flex">';
        t += '<img class="r5" src="' + data.icon + '">';
        t += '<div class="flex-list title"><strong>' + data.gameTitle + "</strong></div>";
        t += "</div>";
        t += "<ul>"
    }
    var gifts = data.gifts;
    for (var i in gifts) {
        t += '<li class="flex">';
        t += '<div class="flex-list">';
        t += '<p class="title">';
        if (parseInt(gifts[i].isDayGift)) {
            t += '<span class="tags-coupon">每日</span>'
        } else if (parseInt(gifts[i].qq_group_num)) {
            t += '<em class="tags-qq">入群</em>'
        } else if (gifts[i].union_code) {
            t += '<span class="tags-coupon">统一码</span>'
        } else if (gifts[i].sum) {
            t += '<em class="tags-normal">普通</em>'
        }
        t += gifts[i].title;
        t += "</p>";
        if (gifts[i].brief_intro.length > 50) {
            t += '<p class="intro">' + gifts[i].brief_intro.replace(/\r\n/g, "<br>").substring(0, 50) + "...<span onclick=\"sdk.confirmDialog('" + gifts[i].brief_intro.replace(/\r\n/g, "<br>") + "',null,null,null,null,'礼包详细')\">详细</span></p>"
        } else {
            t += '<p class="intro">' + gifts[i].brief_intro + "</p>"
        }
        t += "</div>";
        var start = new Date(gifts[i].effect_time * 1e3);
        if (start > new Date) {
            var diff = (start.getTime() - (new Date).getTime()) / 1e3;
            var dd = Math.floor(diff / (24 * 3600));
            var lv1 = diff % (24 * 3600);
            var hh = Math.floor(lv1 / 3600);
            var lv2 = lv1 % 3600;
            var mm = Math.floor(lv2 / 60);
            t += '<a href="javascript:;" class="btn r3 disabled alone">未开放<em>';
            if (dd) {
                t += dd + "天"
            }
            if (hh) {
                t += hh + "小时"
            }
            if (mm) {
                t += mm + "分"
            }
            t += "后开放领取";
            t += "</em></a>"
        } else {
            if (parseInt(gifts[i].isDayGift)) {
                t += '<a href="javascript:;" onclick="getCode(' + id + "," + gifts[i].type + "," + gifts[i].isDayGift + ',this)" class="btn r3">领取</a>'
            } else if (parseInt(gifts[i].qq_group_num)) {
                t += "<a onclick=\"gc.joinGroup('" + gifts[i].qq_group_link + '\')" href="javascript:;" class="btn r3">加群</a>'
            } else if (parseInt(gifts[i].sum) == parseInt(gifts[i].getcount) && !gifts[i].union_code) {
                t += '<a href="javascript:;" class="btn r3 disabled">缺货</a>'
            } else {
                t += '<a href="javascript:;" onclick="getCode(' + id + "," + gifts[i].type + ',null,this)" class="btn r3">';
                if (gifts[i].getCode) {
                    t += "查看"
                } else {
                    t += "领取"
                }
                t += "</a>"
            }
        }
        t += "</li>"
    }
    if (callback) {
        t += "</ul>";
        if (data.giftCnt > 1) {
            t += '<p data-url="' + data.game_url + '" onclick="gc.loadGameInfo(' + data.gameID + ',this)">查看更多礼包(' + (data.giftCnt - 1) + ') <i class="icon-bottom"></i></p>'
        }
        t += "</li>"
    }
    return t
};
gc.addLabels = function (data, isnew) {
    var t = "";
    if (isnew) {
        if (data.indexOf(6) >= 0) {
            t += '<span class="tags-first">首发</span>'
        } else if (data.indexOf(5) >= 0) {
            t += '<span class="tags-new">独家</span>'
        }
    } else {
        if (data.indexOf(5) >= 0) {
            t += '<span class="tags-new">独家</span>'
        }
    } if (data.indexOf(4) >= 0) {
        t += '<span class="tags-coupon">礼包</span>'
    }
    return t
};
gc.loadGameHot = function () {
    if (gc.page.hot) {
        gc.httpGet(gc.apiBaseURL + "getRecoGames&page=" + gc.page.hot + "&pageSize=" + gc.pageSize, function (data) {
            var t = "";
            data = data.data;
            gc.status.hot = false;
            if (data && data.length) {
                for (var i in data) {
                    t += '<li class="flex"><div class="new">';
                    if (Date.parse(gc.time) / 1e3 - data[i].recoEndTime < 7 * 24 * 3600) {
                        t += "<i>新游</i>"
                    }
                    t += '<img class="r5" onclick="gc.loadGameInfo(' + data[i].id + ",this,['热门','icon区域'])\" src=\"" + data[i].icon + '"></div>';
                    t += '<div onclick="gc.loadGameInfo(' + data[i].id + ',this,[\'热门\',\'标题区域\'])" class="flex flex-list flex-v"><p class="title"><span>' + data[i].title + "</span>";
                    t += gc.addLabels(data[i].labels);
                    t += "</p>";
                    if (data[i].brief_intro) {
                        t += "<p>" + data[i].brief_intro + "</p>"
                    }
                    t += "</div><a onclick=\"gc.rebuildGameURL('" + data[i].game_url + "',this,['热门','开始按钮'])\" href=\"javascript:;\" class=\"btn r3\">开始</a></li>"
                }
                $("#hot-box").append(t);
                $("#hot-box li.end").remove();
                if (data.length >= gc.pageSize) {
                    gc.page.hot++;
                    gc.isReady = true;
                    $("#hot-box").append('<li class="end"><fieldset><legend>松开手自动加载</legend></fieldset></li>')
                } else {
                    gc.page.hot = false;
                    $("#hot-box").append('<li class="end"><fieldset><legend>没有更多了</legend></fieldset></li>')
                }
            } else {
                gc.page.hot = false;
                $("#hot-box li.end legend").text("没有更多了")
            }
        })
    }
};
