gc.loadGameInfo = function (id, e, args) {
    gc.httpGet(gc.apiBaseURL + "getGameInfo&id=" + id, function (data) {
        var _data = data.data;
        var gift = data.gift;
        var count = 0;
        var giftList = "";
        var newsList = "";
        var t = "";
        var _gift = {
            gifts: {}
        };
        for (var i = 0; i < gift.length; i++) {
            var gifts = null;
            gifts = gift[i];
            _gift["gifts"][i] = {};
            _gift["gifts"][i] = gifts
        }
        if (e && !args) {
            t += gc.fillGift(_gift, id, _data.game_url);
            $(e).prev().empty().append(t);
            $(e).remove()
        } else {
            location.hash = "game/" + id;
            $(window).scrollTop(0);
            t = '<div class="game-info">';
            t += '<a href="javascript:;" onclick="gc.rebuildGameURL(\'' + _data.game_url + '\')"><img src="' + _data.bg_image + '" ></a>';
            t += '<div class="flex flex-v">';
            t += '<img class="r10" src="' + _data.icon + '">';
            t += "<p>" + _data.title + "</p>";
            t += "<p>" + _data.brief_intro + "</p>";
            t += '<a href="javascript:;" onclick="gc.rebuildGameURL(\'' + _data.game_url + '\')" class="btn">开始游戏</a>';
            t += "</div>";
            t += '<dl class="summary">';
            t += "<dt>游戏简介</dt>";
            t += "<dd>";
            _data.description = _data.description.replace(/\r\n/gi, "|").replace(/\[more]/gi, " ");
            if (_data.description.length > 100) {
                var _des = _data.description.substring(0, 90),
                    _subDes = _data.description.substring(90, _data.description.length);
                if (_des.substr(-1) == "|") {
                    _des = _data.description.substring(0, 89);
                    _subDes = _data.description.substring(89, _data.description.length)
                }
                if (_subDes.substr(-1) == "|") {
                    _subDes = _subDes.substring(0, _subDes.length - 1)
                }
                t += "<p>" + _des.replace(/\|/gi, "<br>") + '<span>...</span><span class="more" data-des="' + _subDes.replace(/\|/gi, "<br>") + '" onclick="gc.showMore(this)">全文</span></p>'
            } else {
                t += "<p>" + _data.description.replace(/\|/gi, "<br>") + "</p>"
            }
            t += "</dd>";
            t += "</dl>";
            if (gift.length > 0) {
                count++;
                giftList += '<li class="gift"><ul>';
                giftList += gc.fillGift(_gift, id, _data.game_url);
                giftList += "</ul></li>"
            }
            if (_data.news.length > 0) {
                count++;
                for (var i in _data.news) {
                    newsList += gc.fillInfo(_data.news[i])
                }
            }
            if (count > 1) {
                t += '<ol class="flex nav">';
                t += '<li onclick="gc.tabLabel(this)" class="flex-list active"><a href="javascript:;">礼包</a></li>';
                t += '<li onclick="gc.tabLabel(this)" class="flex-list"><a href="javascript:;">资讯</a></li>';
                t += "</ol>";
                t += '<div class="show-box">';
                t += '<ol class="">';
                t += giftList;
                t += "</ol>";
                t += '<ol class="exc hide">';
                t += newsList;
                t += "</ol>";
                t += '<div class="flex flex-v infoView hide"></div>';
                t += "</div>"
            } else {
                if (giftList) {
                    t += '<dl class="summary">';
                    t += "<dt>游戏礼包</dt>";
                    t += '<dd class="show-box">';
                    t += '<ol class="active alone">';
                    t += giftList;
                    t += "</ol>";
                    t += "</dd>";
                    t += "</dl>"
                }
                if (newsList) {
                    t += '<dl class="summary">';
                    t += "<dt>游戏资讯</dt>";
                    t += '<dd class="show-box">';
                    t += '<ol class="active">';
                    t += newsList;
                    t += "</ol>";
                    t += '<div class="flex flex-v infoView hide"></div>';
                    t += "</dd>";
                    t += "</dl>"
                }
            } if (_data.developer || _data.recordNum || _data.publisher) {
                t += '<dl class="summary">';
                t += "<dt>游戏信息</dt>";
                t += "<dd>";
                if (_data.developer) {
                    t += "<p>研发商：" + _data.developer + "</p>"
                }
                if (_data.publisher) {
                    t += "<p>发行商：" + _data.publisher + "</p>"
                }
                if (_data.recordNum) {
                    t += "<p>文网游备字：" + _data.recordNum + "</p>"
                }
                t += "</dd>";
                t += "</dl>"
            }
            t += "</div>";
            $("#box-4").empty().append(t)
        }
    }, "json");
    if (args) {
        var index = $(e).parents("li.flex").index() + 1;
        if (args[2]) {
            sdk.combineStat(sdk.uid, "游戏列表", [{
                "广告位": args[0]
            }, {
                "游戏名称": args[2]
            }, {
                "点击位置": args[1]
            }])
        } else {
            sdk.combineStat(sdk.uid, "游戏列表", [{
                "广告位": args[0]
            }, {
                "行号": "第" + index + "行"
            }, {
                "点击位置": args[1]
            }])
        }
    }
};
gc.rebuildGameURL = function (url, e, args) {
    location.href = url;
    return;
    if (args) {
        var index = $(e).parents("li.flex").index() + 1;
        if (args[2]) {
            sdk.combineStat(sdk.uid, "游戏列表", [{
                "广告位": args[0]
            }, {
                "游戏名称": args[2]
            }, {
                "点击位置": args[1]
            }])
        } else {
            sdk.combineStat(sdk.uid, "游戏列表", [{
                "广告位": args[0]
            }, {
                "行号": "第" + index + "行"
            }, {
                "点击位置": args[1]
            }])
        }
    }

    function _buildURL() {
        if (gc.chid) {
            url = sdk.buildURL(url, {
                chid: gc.chid
            });
            if (gc.isTrial) {
                url = sdk.buildURL(url, {
                    trial: 1
                })
            }
        }
        if (gc.subchid) {
            url = sdk.buildURL(url, {
                subchid: gc.subchid
            })
        }
        if (sdk.isWeixin()) {
            url = sdk.buildURL(url, {
                backGC: 1
            })
        }
        if (sdk.isAvuAPP()) {
            if (sdk.isiOS()) {
                sdk.initWebViewJavascriptBridge(function () {
                    if (window.WebViewJavascriptBridge) {
                        window.WebViewJavascriptBridge.callHandler("setRefresh", true);
                        window.WebViewJavascriptBridge.callHandler("setBack", false);
                        window.WebViewJavascriptBridge.callHandler("setGoTo", true);
                        window.WebViewJavascriptBridge.callHandler("setShare", {
                            wxsession: true,
                            wxtimeline: false,
                            qq: false,
                            qzone: false
                        });
                        window.WebViewJavascriptBridge.callHandler("setGoToURL", location.href)
                    }
                })
            } else if (sdk.isAndroid()) {
                window.android.setRefresh(true);
                window.android.setBack(false);
                window.android.setGoTo(true);
                window.android.setShare('{"wxsession": true, "wxtimeline": false, "qq": true, "qzone": true}');
                window.android.setGoToURL(location.href)
            }
        }
        location.href = url
    }
    if (sdk.token) {
        sdk.createCode(sdk.token, function (data) {
            if (data.code) {
                url = sdk.buildURL(url, {
                    code: data.code
                })
            }
            _buildURL()
        })
    } else {
        _buildURL()
    }
};

