            t += '<div class="flex"><i class="icon-img"></i><input class="flex-list" type="number" id="gcImgCode" placeholder="请输入图片验证码"><img id="gcRefreshCode" src=""></div>';
            t += '<div class="flex"><i class="icon-sms"></i><input class="flex-list" type="number" id="gcSmsCode" placeholder="请输入短信验证码"><a href="javascript:;" id="gcGetCode">获取验证码</a></div>'
