    sdk.hostVersion = "4.0 build 201709181450";
    sdk.wxappId = "wx0437200439052cde";
    sdk.wxopenId = "wx22f69b39568e9cb3";
    sdk.qqappId = "200516427";
    sdk.gameId = 0;
    sdk.shareCallback = null;
    sdk.readyCallback = null;
    sdk.readyMsgList = [];
    sdk.code = null;
    sdk.token = null;
    sdk.uid = null;
    sdk.shareDatas = null;
    sdk.isFocus = false;
    sdk.isHideFocus = false;
